import React, { Component } from "react"
import Web3 from "web3"
import detectEthereumProvider from "@metamask/detect-provider"
import KryptoBird from '../abis/KryptoBird.json'


class App extends Component {

    async componentDidMount() {
        await this.loadWeb3()
        await this.loadBlockchainData()
    }

    // first up is to detect ethereum provider
    async loadWeb3() {
        const provider = await detectEthereumProvider()

        if(provider){
            console.log('etherum wallet is connected')
            window.web3 = new Web3(provider)
        } else {
            console.log('no ethereum wallet detected')
        }
    }

    async loadBlockchainData() {
        const web3 = window.web3
        const accounts = await web3.eth.getAccounts()
        this.setState({account:accounts})

        // Blockchain network id
        const networkId = await web3.eth.net.getId()
        const networkData = KryptoBird.networks[networkId]
        if (networkData) {
            const abi = KryptoBird.abi
            const address = networkData.address
            const contract = new web3.eth.Contract(abi, address)
            this.setState({contract})

            // call the total supply of our Krypto Birdz
            const totalSupply = await contract.methods.totalSupply().call()
            this.setState({totalSupply})
            // set up an array to keep track of tokens
            // load KryptoBirdz
            for (let i = 1; i <= totalSupply; ++i) {
                const KryptoBird = await contract.methhods.kryptoBirdz(i - 1).call()
                this.setState({
                    kryptoBirdz:[...this.state.kryptoBirdz, KryptoBird]
                })
            }
        } else {
            window.alert('smart contract not deployed')
        }
    }

    // with miting we are sending information and we need to specify the account
    mint = (kryptoBird) => {
        this.state.contract.methods.mint(kryptoBird).send({from:this.state.account})
        .once('receipt', (receipt) => {
            this.setState({
                kryptoBirdz:[...this.state.kryptoBirdz, KryptoBird]
            })
        })
    }

    constructor(props) {
        super(props);
        this.state = {
            account: '',
            contract: null,
            totalSupply: 0,
            kryptoBirdz: []
        }
    }

    render() {
        return (
            <div>
                <nav className='navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow'>
                    <div class='navbar-brand col-sm-3 col-md-3 mr-0'>
                        Krypto Birdz NFTs (Non Fungible Tokens)
                    </div>
                    <ul className='navbar-nav px-3'>
                        <li className='nav-item text-nowrap'
                        d-none d-sm-none d-sm-block>
                            <small className='text-white'>
                                {this.state.account}
                            </small>
                        </li>
                    </ul>
                </nav>
            </div>
        )
    }
}

export default App;
